           // Wait 50ms between pings (about 20 pings/sec). 29ms should be the shortest delay between pings.\n  Serial.print("Jarak: ");\n  distance = ultrasonic.read();\n  Serial.print(distance);\n  Serial.println("cm");\n  if (distance<=15){\n    moveState=LOW;\n  }\n  else if(distance>=15){\n    moveState=HIGH;\n  }\n  // compare the moveState to its previous state\n  if (moveState != lastMoveState) {\n    // if the state has changed, do the robot move\n    if (moveState == HIGH) {\n\n      digitalWrite(13,LOW);\n      servo1.write(0);\n      servo2.write(0);\n      servo3.write(0);\n    } else {\n      number = random(3);\n      Serial.println(number);\n      digitalWrite(13,HIGH);\n      if(number==0)\n      {\n        servo1.write(0);\n        servo2.write(90);\n        servo3.write(0);\n        delay(2000);\n        }\n      else if(number==1)\n      {\n        servo1.write(0);\n        servo2.write(0);\n        servo3.write(90);\n        delay(2000);\n        }\n      else if(number==2)\n      {\n        servo1.write(90);\n        servo2.write(0);\n        servo3.write(0);\n        delay(2000);\n        }\n    }\n    // Delay a little bit to avoid bouncing\n    delay(50);\n  }\n  // save the current state as the last state, for next time through the loop\n  lastMoveState = moveState;\n\n}\n')))}g.isMDXComponent=!0},94275:(a,e,t)=>{t.d(e,{Z:()=>n});const n=t.p+"assets/images/plsgs-1-38ffbc2fae11438bb0784535aee8fc52.png"},9287:(a,e,t)=>{t.d(e,{Z:()=>n});const n=t.p+"assets/images/plsgs-10-0111846d24b9760c71a09f719ebb8702.png"},41826:(a,e,t)=>{t.d(e,{Z:()=>n});const n=t.p+"assets/images/plsgs-11-ddff4477266523a10839ddd713bc3fd0.png"},68156:(a,e,t)=>{t.d(e,{Z:()=>n});const n=t.p+"assets/images/plsgs-12-a2678e981d54605c50ff0b37a8586778.png"},22636:(a,e,t)=>{t.d(e,{Z:()=>n});const n=t.p+"assets/images/plsgs-13-2c0dd442bdd0af284ec9e5585c0c4e99.png"},76424:(a,e,t)=>{t.d(e,{Z:()=>n});const n=t.p+"assets/images/plsgs-14-b7e703d1bcb5086cbc3c7e96f5b41380.png"},30983:(a,